import './App.css';
import react, { Component } from 'react';
import axios from 'axios';
import Input from './input';
import Button from './button';
import Image from './image';
class App extends Component {
state = {
  name: "test",
  url: 'https://pokeres.bastionbot.org/images/pokemon/1.png'
}

handleChange = (event) => {
  console.log(event.target.value);
  this.setState({name: event.target.value});
}


 button_click = (value) => {
   console.log("The value is " + value);
  let url = 'https://pokeres.bastionbot.org/images/pokemon/' + value+'.png';
  this.setState({url: url});
 // alert(value);


}

get_info = (value) => {
  console.log("The get_inf0 is getting called" + value);
  let url = 'https://pokeapi.co/api/v2/pokemon/' + value + '/';

  axios.get( url)
  .then( response => {

       console.log( response );
  } )
  .catch(error => {
       console.log(error);

  });
}

  render() {

    return(
      <div className="App" style={{backgroundColor: '#1cbab0', height: '100vh', minHeight: '100vh'}}>
        <Input change={this.handleChange}></Input>

        <Button label="Enter" click={() => this.button_click(this.state.name)}></Button>
        <Image source={this.state.url}></Image>

      <Button label="Get Info" click={() => this.get_info(this.state.name)}></Button>

    </div>
    )
  }

}

export default App;
