import logo from './logo.svg';
import './App.css';
import react, { Component } from 'react';
import axios from 'axios';
import Input from './input';
class App extends Component {
state = {
  name: "test",
  url: 'https://pokeres.bastionbot.org/images/pokemon/1.png'
}

handleChange = (event) => {
  console.log(event.target.value);
  this.setState({name: event.target.value});
}


 button_click = (value) => {
   console.log("The value is " + value);
  let url = 'https://pokeres.bastionbot.org/images/pokemon/' + value+'.png';
  this.setState({url: url});
 // alert(value);

}

  render() {

    return(
      <div className="App" style={{backgroundColor: '#1cbab0', height: '100vh', minHeight: '100vh'}}>
        <Input change={() => this.handleChange}></Input>
        <label for="fname">Enter number:</label>

        <div>
        <div>
        <button onClick={() => this.button_click(this.state.name)}>Enter</button>
      </div>

      <img src={this.state.url} alt="test" width="400" height="400"></img>
      </div>


    </div>
    )
  }

}

export default App;
