import logo from './logo.svg';
import './App.css';
import react, { Component } from 'react';


class App extends Component {
state = {
  name: "test"
}

handleChange = (event) => {
  console.log(event.target.value);
  this.setState({name: event.target.value});
}

  render() {


    function handleChange(e){
      console.log(e);
      this.setState({ inputValue: e.target.value });
      this.props.onChange(e);
    }

    function button_click(){
      alert("Here");
    }

    return(
      <div className="App" style={{backgroundColor: '#1cbab0', height: '100vh', minHeight: '100vh'}}>

        <label for="fname">Enter number:</label>
        <input type="text" id="fname" name="fname" onChange={this.handleChange}></input>
        <div>
      {this.state.name}
      </div>
      <div>
        <button onClick={button_click()}>Enter</button>
      </div>

    </div>
    )
  }

}

export default App;
